mapper.select:
select 'true' as QUERYID,
,
from config_info_history where id = #{id,jdbcType=INTEGER}

mapper.insert:
insert into config_info_history (id, config_type, config_file_name, 
  config_file_type, config_file_version, config_file_version_previous, 
  config_file_version_next, config_index, project_id, delete_flag,
  create_time, update_time, config_file_content
  )
values (#{id,jdbcType=INTEGER}, #{configType,jdbcType=VARCHAR}, #{configFileName,jdbcType=VARCHAR}, 
  #{configFileType,jdbcType=VARCHAR}, #{configFileVersion,jdbcType=INTEGER}, #{configFileVersionPrevious,jdbcType=INTEGER},
  #{configFileVersionNext,jdbcType=INTEGER}, #{configIndex,jdbcType=INTEGER}, #{projectId,jdbcType=INTEGER}, #{deleteFlag,jdbcType=INTEGER},
  #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{configFileContent,jdbcType=LONGVARCHAR}
  )
